sqlMap.select:
		SELECT CONCAT('ORDE',
               YEAR(NOW()), 
               IF( LENGTH(MONTH(NOW()))=1,CONCAT('0',MONTH(NOW())),MONTH(NOW()) ),
               MAX( SUBSTR( numeroFila, 11) ) +1 
               ) AS nu_Fila
         FROM paquete_turistico;

		SELECT CONCAT('COTI',
               YEAR(NOW()), 
               IF( LENGTH(MONTH(NOW()))=1,CONCAT('0',MONTH(NOW())),MONTH(NOW()) ),
               MAX( SUBSTR( NU_COTIZA, 11) ) +1 
               ) AS NRO_COTIZA
         FROM COTIZACION;
